<template><v-card class="detail-card"><v-card-title><v-icon v-if="icon" :icon="icon" class="me-2"></v-icon> {{ title }} <v-spacer></v-spacer><slot name="title-actions"></slot></v-card-title><v-divider></v-divider><v-card-text><div v-if="loading" class="text-center my-4"><v-progress-circular indeterminate color="primary"></v-progress-circular><div class="mt-2">Loading details...</div></div><div v-else-if="error" class="text-center my-4"><v-alert type="error" :text="error" variant="tonal"></v-alert></div><template v-else><slot></slot></template></v-card-text><v-card-actions v-if="$slots.actions"><v-spacer></v-spacer><slot name="actions"></slot></v-card-actions></v-card></template><script setup lang="ts">defineProps({title:{type:String,required:true,},icon:{type:String,default:'',},loading:{type:Boolean,default:false,},error:{type:String,default:'',},});</script><style scoped>.detail-card{margin-bottom:1rem}</style>