<template><div class="info-list"><v-list density="compact"><template v-for="(item, index) in items" :key="index"><v-list-item v-if="item.value !==undefined && item.value !==null && item.value !==''"><template v-slot:prepend v-if="item.icon"><v-icon :icon="item.icon"></v-icon></template><v-list-item-title class="font-weight-bold">{{ item.label }}</v-list-item-title><v-list-item-subtitle><slot :name="item.key" :item="item"> {{ item.value }} </slot></v-list-item-subtitle></v-list-item></template><v-list-item v-if="items.length === 0 || isAllEmpty"><v-list-item-subtitle class="text-center text-grey"> No information available </v-list-item-subtitle></v-list-item></v-list></div></template><script setup lang="ts">import{computed}from 'vue';interface InfoItem{key:string;label:string;value?:string|number|null;icon?:string;}const props=defineProps({items:{type:Array as()=>InfoItem[],default:()=>[],},});const isAllEmpty=computed(()=>{return props.items.every((item)=>item.value===undefined||item.value===null||item.value==='');});</script><style scoped>.info-list{width:100%}</style>