<!-- src/components/display/EmptyState.vue --><template><div class="empty-state" :class="containerClass"><div class="empty-state__content"><div v-if="$slots.icon || icon" class="empty-state__icon"><slot name="icon"><v-icon :icon="icon" :size="iconSize" :color="iconColor"></v-icon></slot></div><img v-if="image" :src="image" :alt="title" class="empty-state__image" :style="imageStyle" /><div v-if="$slots.title || title" class="empty-state__title" :class="titleClass"><slot name="title">{{ title }}</slot></div><div v-if="$slots.subtitle || subtitle" class="empty-state__subtitle" :class="subtitleClass"><slot name="subtitle">{{ subtitle }}</slot></div><div v-if="$slots.default" class="empty-state__description"><slot></slot></div><div v-if="$slots.actions || (actionText && actionHandler)" class="empty-state__actions"><slot name="actions"><v-btn v-if="actionText && actionHandler" :color="actionColor" :variant="actionVariant" :size="actionSize" @click="actionHandler" > {{ actionText }} </v-btn></slot></div></div></div></template><script setup lang="ts">import{computed}from 'vue' const props=defineProps({title:{type:String,default:'No data found',},subtitle:{type:String,default:'',},icon:{type:String,default:'mdi-database-off-outline',},iconSize:{type:[Number,String],default:64,},iconColor:{type:String,default:'grey',},image:{type:String,default:'',},imageWidth:{type:[Number,String],default:200,},actionText:{type:String,default:'',},actionColor:{type:String,default:'primary',},actionVariant:{type:String,default:'elevated',},actionSize:{type:String,default:'large',},actionHandler:{type:Function,default:null,},containerClass:{type:String,default:'',},titleClass:{type:String,default:'text-h5 font-weight-bold',},subtitleClass:{type:String,default:'text-subtitle-1 text-medium-emphasis',},})const imageStyle=computed(()=>{const width=typeof props.imageWidth==='number'?`${props.imageWidth}px`:props.imageWidth return{width,maxWidth:'100%',}})</script><style scoped>.empty-state{display:flex;flex-direction:column;align-items:center;justify-content:center;padding:2rem;width:100%;text-align:center}.empty-state__content{max-width:480px}.empty-state__icon{margin-bottom:1.5rem}.empty-state__image{margin-bottom:1.5rem}.empty-state__title{margin-bottom:0.5rem}.empty-state__subtitle{margin-bottom:1rem}.empty-state__description{margin-bottom:1.5rem}.empty-state__actions{display:flex;justify-content:center;flex-wrap:wrap;gap:0.5rem}</style>