<!-- src/components/loaders/SkeletonCard.vue --><template><v-card :class="['skeleton-card', cardClass]" :elevation="elevation"><template v-if="showHeader"><v-skeleton-loader type="card-heading" :loading="true" class="px-4 pt-4" ></v-skeleton-loader></template><v-card-text><v-skeleton-loader :type="contentType" :loading="true" ></v-skeleton-loader><v-skeleton-loader v-if="lines > 0" :type="`text-${lines}`" :loading="true" class="mt-4" ></v-skeleton-loader></v-card-text><template v-if="showActions"><v-divider></v-divider><v-card-actions><v-spacer></v-spacer><v-skeleton-loader type="button" :loading="true" width="80" class="ma-2" ></v-skeleton-loader><v-skeleton-loader type="button" :loading="true" width="80" class="ma-2" ></v-skeleton-loader></v-card-actions></template></v-card></template><script setup lang="ts">defineProps({contentType:{type:String,default:'image',validator:(value:string)=>['image','avatar','article','table','list-item','list-item-avatar','list-item-two-line','list-item-three-line','card'].includes(value),},lines:{type:Number,default:3,},showHeader:{type:Boolean,default:true,},showActions:{type:Boolean,default:false,},elevation:{type:[Number,String],default:1,},cardClass:{type:String,default:'',},})</script>