<template><div class="qualifier-details-page"><PageHeader :title="pageTitle" :subtitle="pageSubtitle" icon="mdi-text-box" :loading="loading" :error="error" ><template v-slot:actions><v-btn color="primary" prepend-icon="mdi-arrow-left" to="/qdb/qualifiers"> Back to Search </v-btn></template></PageHeader><QualifierDetails :qualifier-id="qualifierId" /></div></template><script setup lang="ts">import{ref,computed,onMounted,watch}from 'vue';import{useRoute}from 'vue-router';import{useQdbStore}from '@/stores/autocare/qdb.store.ts';import PageHeader from '@/components/common/PageHeader.vue';import QualifierDetails from '@/components/autocare/qdb/QualifierDetails.vue';const route=useRoute();const qdbStore=useQdbStore();const loading=ref(false);const error=ref('');const qualifierId=computed(()=>route.params.id as string);const pageTitle=computed(()=>{if(qdbStore.currentQualifier){return qdbStore.currentQualifier.qualifier_text||`Qualifier ID: ${qdbStore.currentQualifier.qualifier_id}`;}return 'Qualifier Details';});const pageSubtitle=computed(()=>{if(qdbStore.currentQualifier&&qdbStore.currentQualifier.type){return `Type: ${qdbStore.currentQualifier.type.name || __STRING_7__}`;}return 'Loading qualifier information...';});const clearQualifier=()=>{qdbStore.clearQualifier();};onMounted(()=>{clearQualifier();});watch(()=>route.params.id,()=>{clearQualifier();});</script><style scoped>.qualifier-details-page{width:100%}</style>