<template><v-app><v-app-bar app color="primary" dark><v-app-bar-title> Crown Nexus </v-app-bar-title><v-spacer></v-spacer><template v-if="isLoggedIn"><v-btn to="/" text>Dashboard</v-btn><v-btn to="/products" text>Products</v-btn><v-btn to="/fitments" text>Fitments</v-btn><v-btn @click="logout" text>Logout</v-btn></template><template v-else><v-btn to="/login" text>Login</v-btn></template></v-app-bar><v-main><v-container fluid><router-view /></v-container></v-main><v-footer app><span>&copy; {{ new Date().getFullYear() }} Crown Nexus</span></v-footer></v-app></template><script lang="ts">import{defineComponent,computed,onMounted}from 'vue';import{useAuthStore}from '@/stores/auth';export default defineComponent({name:'App',setup(){const authStore=useAuthStore();const isLoggedIn=computed(()=>authStore.isLoggedIn);const logout=()=>{authStore.logout();};onMounted(()=>{if(isLoggedIn.value){authStore.fetchUserProfile();}});return{isLoggedIn,logout,};},});</script>