<!-- frontend/src/views/ResourcesPage.vue --><template><div class="resources-page"><section class="resources-hero"><v-parallax src="https://images.unsplash.com/photo-1583267746897-2cf415887172?ixlib=rb-1.2.1&auto=format&fit=crop&w=1950&q=80" :height="300" ><div class="hero-overlay"></div><v-container class="hero-content" fluid><v-row align="center" justify="center"><v-col cols="12" md="10" lg="8" class="text-center text-white"><h1 class="text-h2 font-weight-bold mb-4">Resources & Knowledge Base</h1><h2 class="text-h5 mb-2">Expert guides, technical documents, and training materials to support your business</h2></v-col></v-row></v-container></v-parallax></section><section class="py-6 bg-grey-lighten-4"><v-container><v-row justify="center"><v-col cols="12" md="10" lg="8"><v-card elevation="2" class="rounded-pill"><v-card-text class="pa-4"><v-row align="center"><v-col cols="12"><v-text-field v-model="searchQuery" density="comfortable" variant="solo" placeholder="Search resources, documents, guides..." prepend-inner-icon="mdi-magnify" hide-details class="no-border-field rounded-pill" @keyup.enter="searchResources" ><template v-slot:append-inner><v-btn color="primary" class="rounded-pill" @click="searchResources" > Search </v-btn></template></v-text-field></v-col></v-row></v-card-text></v-card></v-col></v-row></v-container></section><!-- Quick Access Links --><section class="py-8"><v-container><v-row justify="center"><v-col cols="12" class="text-center mb-8"><h2 class="text-h4 font-weight-bold mb-2">Quick Access</h2><p class="text-subtitle-1">Find the resources you need, fast</p></v-col></v-row><v-row><v-col v-for="(quickLink, i) in quickAccessLinks" :key="i" cols="6" sm="4" md="3" lg="2"><v-hover v-slot="{ isHovering, props }"><v-card v-bind="props" :elevation="isHovering ? 8 : 2" :class="{ 'on-hover': isHovering }" height="150" @click="navigateTo(quickLink.link)" class="quick-link-card d-flex flex-column align-center justify-center text-center pa-4" ><v-avatar :color="quickLink.color" size="48" class="mb-3"><v-icon size="24" :icon="quickLink.icon" color="white"></v-icon></v-avatar><div class="text-subtitle-1 font-weight-medium">{{ quickLink.title }}</div></v-card></v-hover></v-col></v-row></v-container></section><!-- Featured Resources --><section class="py-8 bg-grey-lighten-4"><v-container><v-row class="mb-8"><v-col cols="12" class="text-center"><h2 class="text-h4 font-weight-bold mb-2">Featured Resources</h2><p class="text-subtitle-1">Popular and recently updated content</p></v-col></v-row><v-row><v-col v-for="(resource, i) in featuredResources" :key="i" cols="12" sm="6" md="4"><v-hover v-slot="{ isHovering, props }"><v-card v-bind="props" :elevation="isHovering ? 8 : 2" :class="{ 'on-hover': isHovering }" height="100%" class="d-flex flex-column" ><v-img :src="resource.image" height="180" cover class="align-end" gradient="to bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,0.7) 100%" ><v-card-title class="text-white pb-1">{{ resource.title }}</v-card-title><v-card-subtitle class="text-white pb-3"><v-chip size="small" color="primary" variant="flat" class="me-2">{{ resource.type }}</v-chip><span class="text-caption">{{ resource.date }}</span></v-card-subtitle></v-img><v-card-text class="flex-grow-1"><p class="text-body-2">{{ resource.description }}</p></v-card-text><v-card-actions><v-spacer></v-spacer><v-btn variant="text" color="primary" :to="resource.link" > View Resource <v-icon end icon="mdi-arrow-right"></v-icon></v-btn></v-card-actions></v-card></v-hover></v-col></v-row></v-container></section><!-- Resource Categories --><section class="py-8"><v-container><v-row><v-col cols="12" md="3" class="mb-8 mb-md-0"><!-- Category Navigation --><h3 class="text-h5 font-weight-bold mb-4">Resource Categories</h3><v-list nav><v-list-item v-for="(category, i) in resourceCategories" :key="i" @click="currentCategory = category.id" :active="currentCategory === category.id" :active-color="category.color" :prepend-icon="category.icon" class="mb-2" ><v-list-item-title>{{ category.name }}</v-list-item-title><template v-slot:append><v-chip size="small" color="grey-lighten-3">{{ category.count }}</v-chip></template></v-list-item></v-list><!-- Filters --><v-divider class="my-6"></v-divider><h3 class="text-h6 font-weight-bold mb-4">Filter Resources</h3><v-select v-model="resourceTypeFilter" label="Resource Type" :items="resourceTypes" variant="outlined" density="comfortable" hide-details class="mb-4" clearable ></v-select><v-select v-model="dateFilter" label="Publication Date" :items="dateFilters" variant="outlined" density="comfortable" hide-details class="mb-4" clearable ></v-select><div class="d-flex justify-end mt-6"><v-btn variant="text" color="primary" prepend-icon="mdi-refresh" @click="resetFilters" size="small" > Reset Filters </v-btn></div></v-col><v-col cols="12" md="9"><!-- Category Title --><v-row class="mb-4"><v-col cols="12"><div class="d-flex align-center"><v-avatar :color="getCurrentCategory.color" size="42" class="me-4"><v-icon :icon="getCurrentCategory.icon" color="white"></v-icon></v-avatar><div><h2 class="text-h4 font-weight-bold">{{ getCurrentCategory.name }}</h2><p class="text-body-1">{{ getCurrentCategory.description }}</p></div></div></v-col></v-row><!-- Resources List --><v-row><v-col cols="12"><v-card v-if="filteredResources.length > 0"><v-data-table :headers="resourceHeaders" :items="filteredResources" :items-per-page="10" class="elevation-1" ><!-- Title Column --><template v-slot:item.title="{ item }"><div class="d-flex align-center"><v-avatar :color="getResourceTypeColor(item.raw.type)" size="32" class="me-3"><v-icon size="16" :icon="getResourceTypeIcon(item.raw.type)" color="white"></v-icon></v-avatar><div><div class="font-weight-medium">{{ item.raw.title }}</div><div class="text-caption text-grey">{{ item.raw.description.substring(0, 60) }}...</div></div></div></template><!-- Type Column --><template v-slot:item.type="{ item }"><v-chip size="small" :color="getResourceTypeColor(item.raw.type)" variant="flat" > {{ item.raw.type }} </v-chip></template><!-- Date Column --><template v-slot:item.date="{ item }"><div class="text-caption">{{ item.raw.date }}</div></template><!-- Actions Column --><template v-slot:item.actions="{ item }"><v-btn icon variant="text" color="primary" size="small" :to="item.raw.link" ><v-icon icon="mdi-open-in-new"></v-icon></v-btn><v-btn icon variant="text" color="grey" size="small" @click="downloadResource(item.raw)" ><v-icon icon="mdi-download"></v-icon></v-btn></template></v-data-table></v-card><!-- Empty State --><v-card v-else class="pa-6 text-center"><v-icon icon="mdi-file-search-outline" size="64" color="grey-lighten-1" class="mb-4"></v-icon><h3 class="text-h6 mb-2">No resources found</h3><p class="text-body-2 mb-4">Try adjusting your filters or search for a different topic.</p><v-btn color="primary" variant="tonal" prepend-icon="mdi-refresh" @click="resetFilters" > Reset Filters </v-btn></v-card></v-col></v-row></v-col></v-row></v-container></section><!-- Popular Downloads --><section class="py-8 bg-grey-lighten-4"><v-container><v-row class="mb-6"><v-col cols="12" class="text-center"><h2 class="text-h4 font-weight-bold mb-2">Popular Downloads</h2><p class="text-subtitle-1">Most frequently accessed resources</p></v-col></v-row><v-row><v-col v-for="(download, i) in popularDownloads" :key="i" cols="12" sm="6" md="3"><v-card class="d-flex flex-column align-center pa-6 text-center" height="100%"><v-avatar :color="getResourceTypeColor(download.type)" size="64" class="mb-4"><v-icon size="32" :icon="getResourceTypeIcon(download.type)" color="white"></v-icon></v-avatar><h3 class="text-h6 font-weight-bold mb-2">{{ download.title }}</h3><p class="text-caption text-grey mb-6 flex-grow-1">{{ download.description }}</p><v-btn color="primary" variant="tonal" prepend-icon="mdi-download" @click="downloadResource(download)" block > Download </v-btn><div class="text-caption mt-2">{{ download.downloads }} downloads</div></v-card></v-col></v-row></v-container></section><!-- Technical Library --><section class="py-8"><v-container><v-row class="mb-6"><v-col cols="12" md="8"><h2 class="text-h4 font-weight-bold mb-2">Technical Library</h2><p class="text-body-1"> Access our comprehensive collection of technical documents, including installation guides, specification sheets, troubleshooting manuals, and more. </p></v-col><v-col cols="12" md="4" class="d-flex align-center justify-center justify-md-end"><v-btn color="primary" variant="tonal" prepend-icon="mdi-book-open-variant" to="/technical-library" > Browse Full Library </v-btn></v-col></v-row><v-row><v-col cols="12"><v-tabs v-model="activeTab" color="primary" align-tabs="center" grow ><v-tab v-for="(tab, i) in techLibTabs" :key="i" :value="tab.id"><v-icon start :icon="tab.icon"></v-icon> {{ tab.name }} </v-tab></v-tabs><v-window v-model="activeTab" class="mt-6"><v-window-item v-for="(tab, i) in techLibTabs" :key="i" :value="tab.id" ><v-row><v-col v-for="(doc, j) in getTechLibDocs(tab.id)" :key="j" cols="12" sm="6" md="4" lg="3"><v-card height="100%" class="d-flex flex-column"><v-card-item><template v-slot:prepend><v-avatar :color="getFileTypeColor(doc.fileType)" size="42"><v-icon size="20" :icon="getFileTypeIcon(doc.fileType)" color="white"></v-icon></v-avatar></template><v-card-title>{{ doc.title }}</v-card-title><v-card-subtitle>{{ doc.fileType.toUpperCase() }} â€¢ {{ doc.size }}</v-card-subtitle></v-card-item><v-card-text class="text-caption flex-grow-1"> {{ doc.description }} </v-card-text><v-card-actions><v-spacer></v-spacer><v-btn variant="text" size="small" prepend-icon="mdi-eye" @click="viewDocument(doc)" > View </v-btn><v-btn variant="text" size="small" prepend-icon="mdi-download" @click="downloadResource(doc)" > Download </v-btn></v-card-actions></v-card></v-col></v-row></v-window-item></v-window></v-col></v-row></v-container></section><!-- Request Resources --><section class="py-8 bg-primary"><v-container><v-row align="center"><v-col cols="12" md="7" class="text-white"><h2 class="text-h4 font-weight-bold mb-4">Can't Find What You Need?</h2><p class="text-body-1 mb-6"> If you're looking for specific information or resources that aren't available in our knowledge base, our technical team is ready to help. Submit a request and we'll create custom documentation or find the resources you need. </p><v-btn color="white" variant="outlined" size="large" class="text-white" prepend-icon="mdi-email-outline" to="/contact?type=resource" > Request a Resource </v-btn></v-col><v-col cols="12" md="5" class="mt-8 mt-md-0"><v-card><v-card-title class="bg-white text-primary"><v-icon icon="mdi-frequently-asked-questions" start class="me-2"></v-icon> Popular Resource Requests </v-card-title><v-list lines="two"><v-list-item v-for="(request, i) in popularRequests" :key="i" :title="request.title" :subtitle="request.description" ><template v-slot:prepend><v-avatar color="primary" variant="tonal"><span class="text-body-2">{{ i + 1 }}</span></v-avatar></template></v-list-item></v-list></v-card></v-col></v-row></v-container></section><!-- Training Resources --><section class="py-8"><v-container><v-row class="mb-8"><v-col cols="12" class="text-center"><h2 class="text-h4 font-weight-bold mb-2">Training Resources</h2><p class="text-subtitle-1">Enhance your team's knowledge and skills</p></v-col></v-row><v-row><v-col v-for="(training, i) in trainingResources" :key="i" cols="12" md="6" lg="4"><v-card height="100%" class="d-flex flex-column"><v-img :src="training.image" height="200" cover ></v-img><v-card-item><v-card-title>{{ training.title }}</v-card-title><v-card-subtitle><v-icon icon="mdi-account-group" size="small" class="me-1"></v-icon> {{ training.participants }} participants <v-icon icon="mdi-clock-outline" size="small" class="ms-3 me-1"></v-icon> {{ training.duration }} </v-card-subtitle></v-card-item><v-card-text class="text-body-2 flex-grow-1"> {{ training.description }} </v-card-text><v-divider></v-divider><v-card-actions><v-spacer></v-spacer><v-btn variant="text" color="primary" :to="training.link" > View Course <v-icon end icon="mdi-arrow-right"></v-icon></v-btn></v-card-actions></v-card></v-col></v-row><v-row class="mt-6"><v-col cols="12" class="text-center"><v-btn color="primary" size="large" variant="tonal" prepend-icon="mdi-school" to="/training" > View All Training </v-btn></v-col></v-row></v-container></section><!-- Resource Subscribe --><section class="py-8 bg-grey-lighten-4"><v-container><v-row justify="center"><v-col cols="12" md="8" lg="6" class="text-center"><h2 class="text-h4 font-weight-bold mb-4">Stay Updated</h2><p class="text-body-1 mb-6"> Subscribe to receive notifications when new resources, technical documents, or training materials are added. </p><v-card elevation="2" class="pa-6"><v-form @submit.prevent="subscribeToResources"><v-row><v-col cols="12" sm="8"><v-text-field v-model="subscribeEmail" label="Email Address" variant="outlined" type="email" density="comfortable" hide-details ></v-text-field></v-col><v-col cols="12" sm="4" class="d-flex align-center"><v-btn type="submit" color="primary" block :loading="subscribeLoading" > Subscribe </v-btn></v-col></v-row><v-row class="mt-4"><v-col cols="12"><div class="text-caption text-grey text-start"> Select the types of resources you're interested in: </div><div class="d-flex flex-wrap mt-2"><v-checkbox v-for="(type, i) in resourceInterests" :key="i" v-model="selectedInterests" :label="type" :value="type" density="compact" hide-details class="me-4" ></v-checkbox></div></v-col></v-row></v-form></v-card></v-col></v-row></v-container></section></div></template><script lang="ts">import{defineComponent,ref,computed}from 'vue';import{useRouter}from 'vue-router';interface QuickAccessLink{title:string;icon:string;color:string;link:string;}interface ResourceCategory{id:string;name:string;description:string;icon:string;color:string;count:number;}interface Resource{id:string;title:string;description:string;type:string;category:string;date:string;link:string;image?:string;fileType?:string;size?:string;downloads?:number;}interface TechLibTab{id:string;name:string;icon:string;}interface PopularRequest{title:string;description:string;}interface TrainingResource{title:string;description:string;image:string;duration:string;participants:string;link:string;}export default defineComponent({name:'ResourcesPage',setup(){const router=useRouter();const searchQuery=ref('');const currentCategory=ref('all');const resourceTypeFilter=ref('');const dateFilter=ref('');const activeTab=ref('installation');const subscribeEmail=ref('');const subscribeLoading=ref(false);const selectedInterests=ref<string[]>(['Technical Documents','Training Materials']);const quickAccessLinks=ref<QuickAccessLink[]>([{title:'Installation Guides',icon:'mdi-tools',color:'primary',link:'/resources/installation'},{title:'Product Catalogs',icon:'mdi-book-open-page-variant',color:'info',link:'/resources/catalogs'},{title:'Technical Specs',icon:'mdi-file-document-outline',color:'success',link:'/resources/specs'},{title:'Video Tutorials',icon:'mdi-video',color:'warning',link:'/resources/videos'},{title:'Training Materials',icon:'mdi-school',color:'error',link:'/resources/training'},{title:'Cross References',icon:'mdi-sync',color:'secondary',link:'/resources/cross-reference'}]);const resourceCategories=ref<ResourceCategory[]>([{id:'all',name:'All Resources',description:'Browse all available resources',icon:'mdi-view-grid',color:'primary',count:287},{id:'technical',name:'Technical Documents',description:'Specifications, diagrams, and technical information',icon:'mdi-file-document-outline',color:'info',count:124},{id:'installation',name:'Installation Guides',description:'Step-by-step installation instructions',icon:'mdi-tools',color:'success',count:78},{id:'catalogs',name:'Product Catalogs',description:'Comprehensive product listings and specifications',icon:'mdi-book-open-page-variant',color:'warning',count:15},{id:'training',name:'Training Materials',description:'Educational resources for staff development',icon:'mdi-school',color:'error',count:42},{id:'marketing',name:'Marketing Materials',description:'Promotional content and marketing assets',icon:'mdi-bullhorn',color:'secondary',count:28}]);const getCurrentCategory=computed(()=>{const category=resourceCategories.value.find(cat=>cat.id===currentCategory.value);return category||resourceCategories.value[0];});const resourceHeaders=[{title:'Title',key:'title',sortable:true},{title:'Type',key:'type',sortable:true},{title:'Date',key:'date',sortable:true},{title:'Actions',key:'actions',sortable:false,align:'end'}];const resourceTypes=['PDF Document','Video Tutorial','Interactive Guide','Specification Sheet','Presentation','Infographic'];const dateFilters=['Last 30 days','Last 90 days','Last 6 months','Last year','Older than 1 year'];const resourceInterests=['Technical Documents','Installation Guides','Training Materials','Product Updates','Marketing Materials'];const featuredResources=ref<Resource[]>([{id:'res-001',title:'2023 Complete Brake Systems Catalog',description:'Comprehensive catalog featuring our complete line of brake components, including specifications and fitment information.',type:'PDF Document',category:'catalogs',date:'Mar 15, 2023',link:'/resources/catalogs/brake-systems-2023',image:'https: }, { id: 'res-002', title: 'Suspension Installation Best Practices', description: 'Expert tips and step-by-step instructions for proper installation of suspension components.', type: 'Video Tutorial', category: 'installation', date: 'Apr 22,2023', link: '/resources/installation/suspension-best-practices', image: 'https:},{id:'res-003',title:'Advanced Diagnostics for Engine Sensors',description:'Technical guide for diagnosing and troubleshooting common engine sensor issues.',type:'Interactive Guide',category:'technical',date:'May 10, 2023',link:'/resources/technical/engine-sensor-diagnostics',image:'https: } ]); const allResources = ref<Resource[]>([ { id: 'tech-001', title: 'OEM Compatibility Guide', description: 'Comprehensive cross-reference guide for OEM part numbers', type: 'PDF Document', category: 'technical', date: 'Jan 15,2023', link: '/resources/technical/oem-compatibility', fileType: 'pdf', size: '4.2 MB' }, { id: 'tech-002', title: 'Material Specifications&Testing Standards', description: 'Detailed information on materials and quality testing', type: 'PDF Document', category: 'technical', date: 'Feb 28,2023', link: '/resources/technical/material-specs', fileType: 'pdf', size: '8.7 MB' }, { id: 'tech-003', title: 'Torque Specifications Chart', description: 'Recommended torque settings for fasteners', type: 'Spreadsheet', category: 'technical', date: 'Mar 10,2023', link: '/resources/technical/torque-specs', fileType: 'xlsx', size: '1.3 MB' }, { id: 'tech-004', title: 'Electrical System Diagnostics', description: 'Troubleshooting guide for electrical components', type: 'Interactive Guide', category: 'technical', date: 'Apr 05,2023', link: '/resources/technical/electrical-diagnostics', fileType: 'html', size: '2.8 MB' }, { id: 'inst-001', title: 'Brake Pad Replacement Guide', description: 'Step-by-step instructions for brake pad installation', type: 'PDF Document', category: 'installation', date: 'Jan 20,2023', link: '/resources/installation/brake-pad-replacement', fileType: 'pdf', size: '3.5 MB' }, { id: 'inst-002', title: 'Suspension Component Installation', description: 'Comprehensive guide for all suspension components', type: 'Video Tutorial', category: 'installation', date: 'Feb 12,2023', link: '/resources/installation/suspension-installation', fileType: 'mp4', size: '85.2 MB' }, { id: 'inst-003', title: 'Engine Sensor Replacement', description: 'Installation instructions for common engine sensors', type: 'PDF Document', category: 'installation', date: 'Mar 30,2023', link: '/resources/installation/engine-sensors', fileType: 'pdf', size: '2.9 MB' }, { id: 'cat-001', title: '2023 Master Parts Catalog', description: 'Complete product catalog with all current offerings', type: 'PDF Document', category: 'catalogs', date: 'Jan 05,2023', link: '/resources/catalogs/master-2023', fileType: 'pdf', size: '42.8 MB' }, { id: 'cat-002', title: 'Suspension Components Catalog', description: 'Specialized catalog for suspension products', type: 'PDF Document', category: 'catalogs', date: 'Feb 15,2023', link: '/resources/catalogs/suspension-2023', fileType: 'pdf', size: '18.3 MB' }, { id: 'train-001', title: 'Brake System Diagnosis', description: 'Training module for diagnosing brake issues', type: 'Presentation', category: 'training', date: 'Jan 25,2023', link: '/resources/training/brake-diagnosis', fileType: 'pptx', size: '15.6 MB' }, { id: 'train-002', title: 'Advanced ADAS Calibration', description: 'Workshop guide for calibrating advanced driver assistance systems', type: 'Video Tutorial', category: 'training', date: 'Mar 18,2023', link: '/resources/training/adas-calibration', fileType: 'mp4', size: '120.5 MB' }, { id: 'mkt-001', title: 'Product Display Guidelines', description: 'Best practices for in-store product displays', type: 'PDF Document', category: 'marketing', date: 'Feb 08,2023', link: '/resources/marketing/display-guidelines', fileType: 'pdf', size: '5.1 MB' }, { id: 'mkt-002', title: 'Digital Marketing Assets', description: 'Collection of images and banners for online use', type: 'ZIP Archive', category: 'marketing', date: 'Apr 01,2023', link: '/resources/marketing/digital-assets', fileType: 'zip', size: '75.4 MB' } ]); const filteredResources = computed(() => { let resources = [...allResources.value]; if (currentCategory.value !== 'all') { resources = resources.filter(resource => resource.category === currentCategory.value); } if (resourceTypeFilter.value) { resources = resources.filter(resource => resource.type === resourceTypeFilter.value); } if (dateFilter.value) { if (dateFilter.value === 'Last 30 days') { resources = resources.filter(resource => resource.date.include