<!-- frontend/src/views/Pricing.vue --><template><div><v-container fluid><v-row class="mb-8"><v-col cols="12" lg="8" class="mx-auto text-center"><h1 class="text-h2 font-weight-bold mb-4">Programs & Pricing</h1><p class="text-subtitle-1 mb-6"> Flexible solutions tailored to your business needs in the automotive aftermarket </p><v-btn-toggle v-model="billingPeriod" density="comfortable" rounded="pill" class="mb-6" variant="elevated" ><v-btn value="monthly">Monthly</v-btn><v-btn value="annual">Annual (Save 15%)</v-btn></v-btn-toggle></v-col></v-row><v-row class="mb-12"><v-col v-for="(plan, index) in plans" :key="plan.id" cols="12" md="4" :class="{'mt-md-n6': plan.highlighted}" ><v-card :elevation="plan.highlighted ? 12 : 2" :color="plan.highlighted ? 'primary' : ''" :class="[plan.highlighted && !isMobile ? 'py-6' : '']" class="h-100 position-relative" ><div v-if="plan.highlighted" class="position-absolute bg-primary text-white px-4 py-1 text-center" style="top: -16px; left: 50%; transform: translateX(-50%); border-radius: 16px; width: 180px;" > MOST POPULAR </div><v-card-item><v-card-title class="text-h4 mb-2">{{ plan.name }}</v-card-title><v-card-subtitle>{{ plan.subtitle }}</v-card-subtitle></v-card-item><v-card-text><div class="text-h3 font-weight-bold mb-1"> ${{ billingPeriod==='annual' ? plan.annualPrice : plan.monthlyPrice }} <span class="text-subtitle-1 font-weight-regular">/month</span></div><div class="text-caption mb-6" v-if="billingPeriod==='annual'"> Billed annually at ${{ plan.annualPrice * 12 }} </div><v-divider class="mb-4"></v-divider><div class="mb-6"><div v-for="(feature, i) in plan.features" :key="i" class="d-flex align-center mb-3" ><v-icon :icon="feature.included ? 'mdi-check-circle' : 'mdi-minus-circle'" :color="feature.included ? 'success' : 'grey'" size="small" class="mr-2" ></v-icon><span :class="{'text-medium-emphasis': !feature.included}"> {{ feature.text }} </span></div></div></v-card-text><v-card-actions class="justify-center pa-6"><v-btn :